//form.js

//npm install pug

var express = require('express');
var bodyParser = require('body-parser');

var app = express();

app.get('/', function(req, res){
   res.render('form');  //the server responds by rendering the form.pug template
});

app.set('view engine', 'pug');  //tells Express to use Pug as the templating engine for rendering views
app.set('views', './views');   //sets the folder where Express will look for the views

app.use(bodyParser.json());    //middleware is used to parse incoming requests with JSON payloads. It allows you to easily handle data sent via the application/json content type
app.use(bodyParser.urlencoded({ extended: true }));  //This middleware is used to parse URL-encoded data (which is commonly used in HTML forms). The extended: true option allows the use of complex objects or arrays when parsing the form data.
//form-urlencoded
// for parsing multipart/form-data

app.post('/', function(req, res){
   console.log(req.body);
   res.send("recieved your request!");
});
app.listen(3000);


